/*globals define, module, require */

((typeof define === "function") ? define :
        function () { "use strict"; require('./nd').apply(module, arguments); })(
    "find_if",
    [
        //no dependencies
    ],
    function () {
        "use strict";
        function find_if(fn, array) {
            /*jslint plusplus: true*/
            var i,
                n = array.length;
            for (i = 0; i < n; ++i) {
                if (fn(array[i])) {
                    return array[i];
                }
            }
        }
        return find_if;
    }
);