/*globals define, module, require */

((typeof define === "function") ? define :
        function () { "use strict"; require('./nd').apply(module, arguments); })(
    "merge",
    [
        //no dependencies
    ],
    function () {
        "use strict";
        function merge(obj, ext) {
            var i;
            for (i in ext) {
                if (ext.hasOwnProperty(i)) {
                    obj[i] = ext[i];
                }
            }
            return obj;
        }
        return merge;
    }
);