//node define helper for AMD + Node design pattern
module.exports = function define(name, reqs, fn) { //node
    "use strict";
    /*jslint unparam: true*/
    /*jshint unused: true*/
    this.exports = fn.apply(null, reqs.map(function (req) {
        switch (req) {
        case "predicates":
            req += "_fast"; //in node, use the larger/faster version
            break;
        }
        return require('./' + req);
    }));
};

