/*globals define, module, require */

((typeof define === "function") ? define :
        function () { "use strict"; require('./nd').apply(module, arguments); })(
    "precedence",
    [
        //no dependencies
    ],
    function () {
        "use strict";
        function precedence(op) {
            switch (op) {
            case "||":
                return 1;
            case "&&":
                return 2;
            case "|":
                return 3;
            case "^":
                return 4;
            case "&":
                return 5;
            case "==":
            case "===":
            case "!=":
            case "!==":
                return 6;
            case "<":
            case ">":
            case "<=":
            case ">=":
            case "in":
            case "instanceof":
                return 7;
            case ">>":
            case "<<":
            case ">>>":
                return 8;
            case "+":
            case "-":
                return 9;
            case "*":
            case "/":
            case "%":
                return 10;
            default:
                return null;
            }
        }
        return precedence;
    }
);